body.messageBody:
{
    "type": "AdaptiveCard",
    "body":[
{
  "columns": [
    {
      "items": @{variables('Adaptivecardbody')},
      "type": "Column"
    }
  ],
  "type": "ColumnSet"
},
        {
            "type": "ColumnSet",
            "columns": [{    "type": "Column",    "items": [        {            "type": "TextBlock",            "size": "Medium",            "weight": "Bolder",            "text": "Incident configuration :",            "wrap": true        }    ],    "width": "auto"}
            ]
        },
        {
            "type": "ColumnSet",
            "columns": [{    "type": "Column",    "items": [        {            "type": "Image",            "style": "Person",            "url": "https://connectoricons-prod.azureedge.net/releases/v1.0.1391/1.0.1391.2130/azuresentinel/icon.png",            "size": "Small"        }    ],    "width": "auto"}
            ]
        },
        {
            "type": "TextBlock",
            "text": "Close Azure Sentinal incident?"
        },
        {
            "choices": [{    "isSelected": true,    "title": "False Positive - Inaccurate Data",    "value": "False Positive - Inaccurate Data"},{    "isSelected": true,    "title": "False Positive - Incorrect Alert Logic",    "value": "False Positive - Incorrect Alert Logic"},{    "title": "True Positive - Suspicious Activity",    "value": "True Positive - Suspicious Activity"},{    "title": "Benign Positive - Suspicious But Expected",    "value": "Benign Positive - Suspicious But Expected"},{    "title": "Undetermined",    "value": "Undetermined"}
            ],
            "id": "incidentStatus",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "Benign Positive - Suspicious But Expected"
        },
        {
            "type": "TextBlock",
            "text": "Change Azure Sentinel Incident Severity?"
        },
        {
            "choices": [{      "title": "High",    "value": "High"},{    "title": "Medium",    "value": "Medium"},{    "title": "Low",    "value": "Low"},{    "title": "Don't change",    "value": "same"}
            ],
            "id": "incidentSeverity",
            "style": "compact",
            "type": "Input.ChoiceSet",
            "value": "@{triggerBody()?['object']?['properties']?['severity']}"
        }
],
	"actions": @{variables('DeviceActions')},
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2"
}

{
    "type": "AdaptiveCard",
    "body": [
  {
    "size": "large",
    "text": "Suspicious Device - Azure Sentinel",
    "type": "TextBlock",
    "weight": "bolder",
    "wrap": true
  },
  {
    "text": "Possible comprised device detected by the provider ",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "text": " @{triggerBody()?['object']?['properties']?['severity']} Incident @{triggerBody()?['object']?['properties']?['title']} ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": " Incident No :  @{triggerBody()?['object']?['properties']?['incidentNumber']} ",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "Incident description",
    "type": "TextBlock",
    "weight": "Bolder",
    "wrap": true
  },
  {
    "text": "@{triggerBody()?['object']?['properties']?['description']}",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "text": "[Click here to view the Incident](@{triggerBody()?['object']?['properties']?['incidentUrl']})",
    "type": "TextBlock",
    "wrap": true
  },
  {
    "size": "Small",
    "style": "Person",
    "type": "Image",
    "url": "https://uploads4.craft.co/uploads/company/logo/852xx/85212/normal_1171b7695370eb94.jpg"
  },
        {
            "type": "TextBlock",
            "weight": "Bolder",
            "text": "Below is the summary of actions taken by SOC",
            "wrap": true
        },
 {
            "type": "TextBlock",
            "weight": "Bolder",
             "text": "Hostname : @{body('Entities_-_Get_Hosts')?['Hosts']?[0]?['HostName']}",
             "wrap": true
        },
    {
  "columns": [
    {
      "items": @{variables('ActionSummary')},
      "type": "Column",
      "wrap": true
    }
  ],
  "separator": "true",
  "type": "ColumnSet",
  "width": "stretch"
}
 ],
"width":"auto",
 "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
 "version": "1.2"
}
